def calculate_kinetic_energy(mass: float, velocity: float) -> float:
    if mass < 0 or velocity < 0:
        return 0.0
    return 0.5 * mass * velocity * velocity

def calculate_spring_potential_energy(spring_constant: float, compression_distance: float) -> float:
    if spring_constant < 0 or compression_distance < 0:
        return 0.0
    return 0.5 * spring_constant * compression_distance * compression_distance

def angle_sum_property(angles):
    if len(angles) != 2:
        return 'Error: Input must be a list of two angles.'
    angle1, angle2 = angles
    if angle1 <= 0 or angle2 <= 0:
        return 'Error: Angles must be positive.'
    if angle1 + angle2 >= 180:
        return 'Error: Sum of angles must be less than 180.'
    return 180 - (angle1 + angle2)
