def read_diagram_angle(diagram_image):
    if diagram_image == 'diagram_with_angle_102':
        return 102
    elif diagram_image == 'diagram_with_no_clear_angle':
        return None
    return None

def map_angle_to_option(angle_value, options):
    for option in options:
        if str(angle_value) == option['text']:
            return option['label']
    return None
