def read_diagram_value(diagram):
    if diagram == 'diagram_with_angle_102':
        return 102.0
    elif diagram == 'diagram_with_no_marked_angle':
        return 'error'
    return 'error'

def map_value_to_option(value, options):
    for option in options:
        if str(value) == option['text']:
            return option['label']
    return 'error'
